import "./calculator.css";
import Display from "../Display/display";
import Button from "../Button/button";
import { useState } from "react";

const Calculator = () => {
  const [firstNum, setFirstNum] = useState("");
  const [lastNum, setLastNum] = useState("");
  const [operator, setOperator] = useState("");
  const [result, setResult] = useState("0");

  const handleResult = () => {};

  const handleInput = (value) => {
    if (!isNaN(value) || value === ".") {
      if (value === ".") {
        if (!operator && firstNum.includes(".")) return;
        if (operator && lastNum.includes(".")) return;
        if (!operator && !firstNum) {
          setFirstNum("0.");
          return;
        }
        if (operator && !lastNum) {
          setLastNum("0.");
          return;
        }
      }

      if (!operator) {
        setFirstNum((prev) => (prev === "0" ? value : prev + value));
      } else if (operator) {
        setLastNum((prev) => (prev === "0" ? value : prev + value));
      }
    } else if (["+", "-", "*", "/", "%"].includes(value)) {
      if (!firstNum) {
        setFirstNum("0");
        setOperator(value);
      } else if (firstNum && lastNum) {
        handleResult();
        setOperator(value);
        return;
      } else {
        setOperator(value);
      }
    }
  };

  const handleAllClear = () => {
    setFirstNum("");
    setLastNum("");
    setOperator("");
    setResult("0");
  };

  const handeleClick = (value) => {
    if (value === "=") {
      handleResult();
    } else if (value === "AC") {
      handleAllClear();
    } else {
      handleInput(value);
    }
  };

  return (
    <div className="calculator">
      <Display input={firstNum + operator + lastNum} result={result} />
      <Button onButtonClick={handeleClick} />
    </div>
  );
};

export default Calculator;
