import "./button.css";

const ButtonItem = ({ btn, onButtonClick, firstNum, lastNum }) => {
  let className = "number";
  if (["+", "-", "*", "/", "="].includes(btn)) className = "operator";
  if (["AC", "+/-", "%"].includes(btn)) className = "function";
  if (btn === "0") className += " zero";

  let displayText = btn;
  if (displayText === "AC") {
    if (!firstNum && !lastNum) {
      displayText = "AC";
    } else {
      displayText = "C";
    }
  }

  const handeleClick = () => {
    onButtonClick(btn);
  };

  return (
    <button className={className} onClick={handeleClick}>
      {displayText}
    </button>
  );
};

export default ButtonItem;
